tables.table_schema:
CREATE TABLE phishing_victims (
    victim_id SERIAL PRIMARY KEY,
    victim_name VARCHAR(50) NOT NULL,
    victim_email VARCHAR(100) UNIQUE NOT NULL,
    amount_lost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE victim_information (
    victim_id INT PRIMARY KEY,
    victim_name VARCHAR(255),
    email_address VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE phishing_attempts (
    attempt_id INT PRIMARY KEY,
    victim_id INT REFERENCES victim_information(victim_id),
    timestamp TIMESTAMP,
    success BOOLEAN
);

CREATE TABLE financial_transaction (
    transaction_id INT PRIMARY KEY,
    victim_id INT REFERENCES victim_information(victim_id),
    amount DECIMAL,
    transaction_date DATE
);
