tables.table_schema:
CREATE TABLE network_traffic_data (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    source_ip VARCHAR(15),
    destination_ip VARCHAR(15),
    protocol VARCHAR(5)
);

CREATE TABLE security_events (
    event_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    event_type VARCHAR(50),
    description TEXT
);

CREATE TABLE regulatory_requirements (
    requirement_id UUID PRIMARY KEY,
    requirement_name VARCHAR(50),
    description TEXT
);

CREATE TABLE breach_detection (
    detection_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    detection_method VARCHAR(50),
    result VARCHAR(50)
);

CREATE TABLE breach_investigation (
    investigation_id UUID PRIMARY KEY,
    detection_id UUID REFERENCES breach_detection(detection_id),
    investigator VARCHAR(50),
    findings TEXT
);
