tables.table_schema:
CREATE TABLE sensitive_data (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    ssn VARCHAR(11)
);

CREATE TABLE encryption_keys (
    key_id SERIAL PRIMARY KEY,
    encryption_key VARCHAR(32)
);

CREATE TABLE encryption_logs (
    log_id SERIAL PRIMARY KEY,
    table_name VARCHAR(50),
    action VARCHAR(10),
    log_timestamp TIMESTAMP
);

CREATE TABLE EncryptedData (
    id INT PRIMARY KEY,
    sensitive_data VARCHAR(255) NOT NULL,
    encryption_key VARCHAR(255) NOT NULL
);

CREATE TABLE EncryptionLogs (
    log_id INT PRIMARY KEY,
    data_id INT NOT NULL,
    encryption_status VARCHAR(50) NOT NULL,
    encryption_timestamp TIMESTAMP
);

CREATE TABLE EnforcementPolicies (
    policy_id INT PRIMARY KEY,
    data_type VARCHAR(50) NOT NULL,
    compliance_level VARCHAR(10) NOT NULL
);

CREATE TABLE ComplianceReports (
    report_id INT PRIMARY KEY,
    policy_id INT NOT NULL,
    report_text TEXT NOT NULL,
    report_timestamp TIMESTAMP
);

CREATE TABLE sensitive_data (id INT PRIMARY KEY, data VARCHAR(255));
CREATE TABLE encryption_keys (key_id INT PRIMARY KEY, key_value VARCHAR(255));
CREATE TABLE encryption_logs (log_id INT PRIMARY KEY, date_time DATETIME, action VARCHAR(50));
CREATE TABLE user_permissions (user_id INT, permission VARCHAR(50));
CREATE TABLE audit_trail (audit_id INT PRIMARY KEY, date_time DATETIME, action VARCHAR(50));
