tables.table_schema:
CREATE TABLE regulatory_guidelines (
  guideline_id INT PRIMARY KEY,
  guideline_text TEXT,
  compliance_status BOOLEAN
);

CREATE TABLE responders (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    role VARCHAR(100),
    email VARCHAR(255)
);

CREATE TABLE regulatory_guidelines (
    id INT PRIMARY KEY,
    guideline_name VARCHAR(255),
    description TEXT
);

CREATE TABLE IncidentDetail (
  detail_id INT PRIMARY KEY,
  incident_id INT REFERENCES SecurityIncidents(incident_id),
  detail_description TEXT,
  affected_records INT
);

CREATE TABLE IncidentResponseTeam (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  team_lead VARCHAR(50),
  team_members INT
);

CREATE TABLE RegulatoryGuidelines (
  guideline_id INT PRIMARY KEY,
  guideline_name VARCHAR(50),
  guideline_description TEXT,
  guideline_link VARCHAR(255)
);

CREATE TABLE IncidentLogs (
  log_id INT PRIMARY KEY,
  incident_id INT REFERENCES SecurityIncidents(incident_id),
  log_date TIMESTAMP,
  log_description TEXT
);
