tables.table_schema:
CREATE TABLE compliance_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_data TEXT
);

CREATE TABLE regulatory_bodies (
    body_id INT PRIMARY KEY,
    body_name VARCHAR(50),
    body_type VARCHAR(50)
);

CREATE TABLE RegulatoryBodies (
    body_id INT PRIMARY KEY,
    body_name VARCHAR(255) NOT NULL,
    region VARCHAR(100) NOT NULL
);
