tables.table_schema:
CREATE TABLE stocks (
  stock_id INT PRIMARY KEY,
  stock_name VARCHAR,
  company_name VARCHAR,
  sector VARCHAR
);

CREATE TABLE InvestmentFirms (
    firm_id INT PRIMARY KEY,
    firm_name VARCHAR,
    firm_type VARCHAR
);

CREATE TABLE StockPrices (
    stock_id INT PRIMARY KEY,
    stock_symbol VARCHAR,
    price DECIMAL,
    date DATE
);

CREATE TABLE StockAnalysis (
    analysis_id INT PRIMARY KEY,
    stock_id INT,
    analysis_results TEXT,
    FOREIGN KEY (stock_id) REFERENCES StockPrices(stock_id)
);

CREATE TABLE investment_firms (
 firm_id INT PRIMARY KEY,
 firm_name VARCHAR(255) NOT NULL
);

CREATE TABLE stock_price_data (
 stock_id INT PRIMARY KEY,
 stock_symbol VARCHAR(10) NOT NULL,
 price DECIMAL(10, 2) NOT NULL,
 date DATE NOT NULL
);
