tables.table_schema:
CREATE TABLE market_data (
  symbol VARCHAR(10),
  price DECIMAL(10, 2),
  volume INT,
  last_updated TIMESTAMP
);

CREATE TABLE Market_Data (
    Symbol VARCHAR(10) PRIMARY KEY,
    Price DECIMAL(10,2),
    Volume INT,
    Last_Trade_Time TIMESTAMP
);

CREATE TABLE stocks (
    symbol VARCHAR(10),
    price DECIMAL,
    volume INT,
    timestamp TIMESTAMP
);

CREATE TABLE trades (
    trade_id SERIAL PRIMARY KEY,
    symbol VARCHAR(10),
    trade_price DECIMAL,
    trade_volume INT,
    trade_date DATE
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50),
    created_at TIMESTAMP
);

CREATE TABLE MarketIndices (
    index_name VARCHAR PRIMARY KEY,
    value DECIMAL,
    change DECIMAL,
    percentage_change DECIMAL
);

CREATE TABLE Watchlist (
    watchlist_id SERIAL PRIMARY KEY,
    symbol VARCHAR,
    added_date DATE,
    notes TEXT
);
