tables.table_schema:
CREATE TABLE budget_plans (
 plan_id BIGINT PRIMARY KEY,
 plan_name TEXT,
 start_date DATE,
 end_date DATE
);

CREATE TABLE collaborators (
 collaborator_id BIGINT PRIMARY KEY,
 collaborator_name TEXT,
 email TEXT
);

CREATE TABLE budget_plans (
	id INT PRIMARY KEY,
	plan_name TEXT,
	owner_id INT,
	created_at TIMESTAMP
);

CREATE TABLE users (
	user_id INT PRIMARY KEY,
	username TEXT,
	email TEXT,
	phone_number TEXT
);

CREATE TABLE budget_share (
	share_id INT PRIMARY KEY,
	budget_id INT,
	shared_with INT,
	permission_level INT
);
