tables.table_schema:
CREATE TABLE credit_scores (
    user_id INT PRIMARY KEY,
    score INT,
    date DATE
);

CREATE TABLE credit_scores (
    id UUID PRIMARY KEY,
    user_id UUID,
    change_date TIMESTAMP,
    score INT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    full_name VARCHAR,
    email VARCHAR UNIQUE
);

CREATE TABLE score_improvements (
    improvement_id UUID PRIMARY KEY,
    user_id UUID,
    improvement_date TIMESTAMP,
    new_score INT
);

CREATE TABLE credit_score_changes (
    id INT PRIMARY KEY,
    date DATE,
    credit_score INT
);

CREATE TABLE credit_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE credit_history (
    history_id INT PRIMARY KEY,
    customer_id INT,
    date DATE,
    credit_score INT
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    date DATE,
    amount DECIMAL(10,2)
);

CREATE TABLE CreditScoreHistory (
  id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  credit_score INT NOT NULL,
  change_date DATE
);

CREATE TABLE User (
  user_id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL
);

CREATE TABLE CreditInquiry (
  inquiry_id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  inquiry_date DATE
);

CREATE TABLE CreditReport (
  report_id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  report_date DATE,
  report_text TEXT
);

CREATE TABLE CreditLimit (
  limit_id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  credit_limit INT NOT NULL
);
