tables.table_schema:
CREATE TABLE debt_to_income_ratios (
id INT PRIMARY KEY,
user_id INT,
monthly_income DECIMAL,
monthly_debt DECIMAL
);

CREATE TABLE FinancialData (
    financial_data_id INT PRIMARY KEY,
    customer_id INT REFERENCES Customer(customer_id),
    income DECIMAL(10, 2),
    debts DECIMAL(10, 2)
);

CREATE TABLE payment(
    payment_id INT PRIMARY KEY,
    loan_id INT,
    payment_amount DECIMAL,
    payment_date DATE
);

CREATE TABLE debt_to_income_ratio(
    customer_id INT PRIMARY KEY,
    debt DECIMAL,
    income DECIMAL,
    dti_ratio DECIMAL
);

CREATE TABLE Customers (
  customer_id UUID PRIMARY KEY,
  customer_name VARCHAR(50),
  income DECIMAL(10, 2),
  debt DECIMAL(10, 2)
);

CREATE TABLE IncomeThresholds (
  threshold_id UUID PRIMARY KEY,
  threshold_amount DECIMAL(10, 2)
);

CREATE TABLE DebtThresholds (
  threshold_id UUID PRIMARY KEY,
  threshold_amount DECIMAL(10, 2)
);

CREATE TABLE DebtToIncomeRatios (
  ratio_id UUID PRIMARY KEY,
  customer_id UUID,
  debt_to_income_ratio DECIMAL(10, 2)
);
