tables.table_schema:
CREATE TABLE market_data (
    date DATE,
    stock_symbol VARCHAR(50),
    closing_price DECIMAL,
    volume INT
);

CREATE TABLE investment_strategies (
    strategy_id INT,
    strategy_name VARCHAR(50),
    description TEXT
);

CREATE TABLE portfolio_performance (
    portfolio_id INT,
    strategy_id INT,
    date DATE,
    returns DECIMAL
);

CREATE TABLE historical_stock_data (
 stock_symbol VARCHAR(10),
 date DATE,
 closing_price FLOAT,
 trading_volume INT
);

CREATE TABLE sector_performance (
 sector_name VARCHAR(50),
 date DATE,
 performance_index FLOAT
);

CREATE TABLE investment_portfolio (
 portfolio_id INT,
 stock_symbol VARCHAR(10),
 shares_owned INT,
 investment_amount FLOAT
);

CREATE TABLE prediction_analysis (
 analysis_id INT,
 date DATE,
 predicted_trend VARCHAR(50),
 confidence_level FLOAT
);
