tables.table_schema:
CREATE TABLE retirement_account (
    account_id INT PRIMARY KEY,
    user_id INT,
    balance DECIMAL,
    threshold DECIMAL
);

CREATE TABLE UserRetirementAccount (
    user_id INT,
    account_balance DECIMAL,
    alert_threshold DECIMAL
);

CREATE TABLE AlertLog (
    user_id INT,
    alert_timestamp TIMESTAMP,
    alert_message TEXT
);

CREATE TABLE retirement_accounts (
    account_id INT PRIMARY KEY,
    user_id INT,
    balance DECIMAL(18, 2)
);

CREATE TABLE thresholds (
    threshold_id INT PRIMARY KEY,
    threshold_amount DECIMAL(18, 2),
    notification_type VARCHAR(50)
);

CREATE TABLE notifications (
    notification_id INT PRIMARY KEY,
    account_id INT,
    threshold_id INT,
    notification_date DATETIME
);

CREATE TABLE RetirementAccount (
    account_id INT PRIMARY KEY,
    user_id INT,
    balance DECIMAL,
    threshold DECIMAL
);

CREATE TABLE User (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE Notification (
    notification_id INT PRIMARY KEY,
    user_id INT,
    message VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE ThresholdSetting (
    setting_id INT PRIMARY KEY,
    user_id INT,
    threshold DECIMAL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    retire_account_balance DECIMAL(10,2) NOT NULL
);

CREATE TABLE thresholds (
    threshold_id INT PRIMARY KEY,
    threshold_amount DECIMAL(10,2) NOT NULL,
    notification_message TEXT NOT NULL
);
