tables.table_schema:
CREATE TABLE hedge_fund_positions (
    position_id INT PRIMARY KEY,
    fund_id INT,
    stock_symbol VARCHAR(255),
    position_size DECIMAL
);

CREATE TABLE stock_prices (
    stock_id INT PRIMARY KEY,
    stock_symbol VARCHAR(255),
    price DECIMAL,
    market_cap DECIMAL
);

CREATE TABLE HedgeFundInvestments (
    InvestmentID INT PRIMARY KEY,
    HedgeFundName VARCHAR(50),
    InvestmentAmount DECIMAL(10,2),
    InvestmentDate DATE
);

CREATE TABLE HedgeFundInvestors (
   investor_id INT PRIMARY KEY,
   investor_name VARCHAR(50),
   investment_amount DECIMAL(10,2),
   investment_date DATE
);

CREATE TABLE StockPrices (
   stock_symbol VARCHAR(10) PRIMARY KEY,
   price DECIMAL(10,2),
   price_date DATE
);

CREATE TABLE MarketTrends (
   trend_id INT PRIMARY KEY,
   trend_description VARCHAR(255),
   trend_date DATE
);

CREATE TABLE TradingOpportunities (
   opportunity_id INT PRIMARY KEY,
   opportunity_description VARCHAR(255),
   opportunity_date DATE
);
