tables.table_schema:
CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  address VARCHAR(100),
  debt_amount DECIMAL(10, 2)
);

CREATE TABLE debt_management_agencies (
 agency_id INT PRIMARY KEY,
 agency_name VARCHAR(255),
 address VARCHAR(255),
 phone_number VARCHAR(20)
);

CREATE TABLE users (
  id UUID PRIMARY KEY,
  username VARCHAR(50),
  password VARCHAR(50),
  role VARCHAR(20)
);

CREATE TABLE debtors ( 
    id INT PRIMARY KEY, 
    name VARCHAR(50), 
    address VARCHAR(100), 
    debt_amount DECIMAL(10, 2) 
);

CREATE TABLE users ( 
    user_id INT PRIMARY KEY, 
    username VARCHAR(50), 
    password VARCHAR(100) 
);
