tables.table_schema:
CREATE TABLE debtor_information (
  debtor_id INT PRIMARY KEY,
  credit_score INT,
  income_information JSON
);

CREATE TABLE debtors (
    debtor_id INT PRIMARY KEY,
    full_name VARCHAR(100),
    credit_score INT,
    income DECIMAL
);

CREATE TABLE external_data (
    data_id INT PRIMARY KEY,
    debtor_id INT,
    credit_score INT,
    income DECIMAL,
    source VARCHAR(100)
);

CREATE TABLE CreditScores (
    debtor_id INT,
    credit_score INT
);

CREATE TABLE IncomeInformation (
    debtor_id INT,
    income DOUBLE
);

CREATE TABLE RepaymentCapacity (
    debtor_id INT,
    capacity VARCHAR
);

CREATE TABLE DebtorInformation (
    debtor_id INT,
    name VARCHAR,
    address VARCHAR
);

CREATE TABLE DebtDetails (
    debtor_id INT,
    debt_amount DOUBLE,
    repayment_date DATE
);
