tables.table_schema:
CREATE TABLE `debt_management_teams` (
  `team_id` INT PRIMARY KEY,
  `team_name` VARCHAR(50) NOT NULL,
  `team_lead` VARCHAR(50) NOT NULL
);

CREATE TABLE `recovery_rates` (
  `rate_id` INT PRIMARY KEY,
  `team_id` INT,
  `recovery_rate` DECIMAL(5,2) NOT NULL,
  FOREIGN KEY (`team_id`) REFERENCES `debt_management_teams`(`team_id`)
);

CREATE TABLE `aging_of_debts` (
  `debt_id` INT PRIMARY KEY,
  `debt_amount` DECIMAL(10,2) NOT NULL,
  `debt_age` INT NOT NULL
);

CREATE TABLE debtors (
    debtor_id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    address VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE payments (
    payment_id SERIAL PRIMARY KEY,
    debt_id INT REFERENCES debts(debt_id),
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE teams (
    team_id SERIAL PRIMARY KEY,
    team_name VARCHAR(100),
    team_lead VARCHAR(100),
    location VARCHAR(100)
);
