tables.table_schema:
CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    savings_plan_preference VARCHAR(50),
    behavior_score INT
);

CREATE TABLE SavingsPlanSegments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50),
    criteria VARCHAR(100),
    additional_notes TEXT
);

CREATE TABLE CustomerDetails (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    savings_plan VARCHAR(50) NOT NULL
);

CREATE TABLE SavingsBehavior (
    behavior_id SERIAL PRIMARY KEY,
    customer_id INT REFERENCES CustomerDetails(customer_id),
    behavior_type VARCHAR(50) NOT NULL,
    behavior_details TEXT
);

CREATE TABLE PlanPreferences (
    preference_id SERIAL PRIMARY KEY,
    customer_id INT REFERENCES CustomerDetails(customer_id),
    preference_type VARCHAR(50) NOT NULL,
    preference_description TEXT
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    savings_plan VARCHAR(50)
);

CREATE TABLE SavingsPlans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50),
    annual_contribution DECIMAL(10,2),
    interest_rate FLOAT
);

CREATE TABLE Transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    amount DECIMAL(10,2),
    transaction_date DATE
);

CREATE TABLE CustomerSegmentation (
    customer_id INT,
    segment VARCHAR(50)
);

CREATE TABLE PlanStatistics (
    plan_id INT,
    total_contributions DECIMAL(10,2),
    average_interest_rate FLOAT
);
