tables.table_schema:
CREATE TABLE customers (
    customer_id UUID PRIMARY KEY, 
    customer_name VARCHAR(255), 
    email VARCHAR(255), 
    savings_plan_goal DECIMAL
);

CREATE TABLE notifications (
    notification_id UUID PRIMARY KEY, 
    customer_id UUID, 
    notification_text VARCHAR(255), 
    notification_time TIMESTAMP
);

CREATE TABLE Customers (
    customer_id UUID PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    savings_goal DECIMAL NOT NULL
);

CREATE TABLE SavingsProgress (
    progress_id UUID PRIMARY KEY,
    customer_id UUID REFERENCES Customers(customer_id),
    current_savings DECIMAL NOT NULL,
    goal_reached BOOLEAN NOT NULL
);

CREATE TABLE Notifications (
    notification_id UUID PRIMARY KEY,
    customer_id UUID REFERENCES Customers(customer_id),
    notification_message VARCHAR(255) NOT NULL,
    notification_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE SavingsPlans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50) NOT NULL,
    goal_amount DECIMAL(10,2) NOT NULL
);
