tables.table_schema:
CREATE TABLE MergersAndAcquisitions (
    deal_id INT PRIMARY KEY,
    deal_name VARCHAR(255),
    client_name VARCHAR(255),
    transaction_amount DECIMAL
);

CREATE TABLE deal_progress (
    deal_id INT,
    progress_status VARCHAR(50) NOT NULL,
    update_date DATE,
    FOREIGN KEY (deal_id) REFERENCES mergers(id)
);

CREATE TABLE mergers_acquisitions (
    deal_id INT PRIMARY KEY,
    company_name TEXT,
    deal_status TEXT,
    deal_value DECIMAL
);

CREATE TABLE investment_banks (
    bank_id INT PRIMARY KEY,
    bank_name TEXT,
    headquarters_location TEXT,
    number_of_deals INT
);

CREATE TABLE deal_advisors (
    advisor_id INT PRIMARY KEY,
    advisor_name TEXT,
    bank_id INT,
    deal_id INT
);

CREATE TABLE deal_progress (
    progress_id INT PRIMARY KEY,
    deal_id INT,
    progress_description TEXT,
    progress_date DATE
);
