tables.table_schema:
CREATE TABLE credit_card_rewards_points (
  user_id INT PRIMARY KEY,
  total_points INT,
  points_expiry_date TIMESTAMP,
  points_usage_description TEXT
);

CREATE TABLE credit_card_rewards (
 id INT PRIMARY KEY,
 credit_card_number VARCHAR(16),
 rewards_points INT
);

CREATE TABLE rewards_optimization (
 id INT PRIMARY KEY,
 credit_card_id INT,
 optimal_strategy VARCHAR(255)
);

CREATE TABLE CreditCardRewardsPoints (
    card_number INT PRIMARY KEY,
    points_balance INT,
    rewards_program VARCHAR
);

CREATE TABLE Transactions (
    transaction_id INT PRIMARY KEY,
    card_number INT,
    transaction_amount INT,
    transaction_date DATE
);

CREATE TABLE RewardsRedemption (
    redemption_id INT PRIMARY KEY,
    card_number INT,
    redeemed_points INT,
    redemption_date DATE
);

CREATE TABLE Rewards_Programs (
    program_id UUID PRIMARY KEY,
    program_name VARCHAR(50),
    credit_card VARCHAR(50),
    reward_rate DECIMAL
);

CREATE TABLE Transactions (
    transaction_id UUID PRIMARY KEY,
    transaction_date DATE,
    transaction_amount DECIMAL,
    program_id UUID REFERENCES Rewards_Programs(program_id)
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(50)
);

CREATE TABLE Points_Balance (
    user_id UUID REFERENCES Users(user_id),
    program_id UUID REFERENCES Rewards_Programs(program_id),
    points_balance INTEGER
);

CREATE TABLE rewards_program (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255) NOT NULL,
  point_value DECIMAL(10, 2) NOT NULL
);
