tables.table_schema:
CREATE TABLE alerts_for_budget_variances (
  alert_id INT PRIMARY KEY,
  alert_name VARCHAR(50),
  threshold_amount DECIMAL,
  alert_type VARCHAR(20)
);

CREATE TABLE financial_milestones (
  milestone_id INT PRIMARY KEY,
  milestone_name VARCHAR(50),
  target_date DATE,
  completion_status BOOLEAN
);

CREATE TABLE budget_alerts (
  alert_id INT PRIMARY KEY,
  alert_type VARCHAR(50),
  budget_amount DECIMAL(10, 2),
  variance_threshold DECIMAL(5, 2)
);

CREATE TABLE financial_milestones (
  milestone_id INT PRIMARY KEY,
  milestone_name VARCHAR(100),
  milestone_target DECIMAL(10, 2),
  completion_date DATE
);

CREATE TABLE AlertReceipts (
    receipt_id UUID PRIMARY KEY,
    alert_id UUID,
    receipt_date TIMESTAMP,
    read_status BOOLEAN
);

CREATE TABLE UserProfiles (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE AlertLogs (
    log_id UUID PRIMARY KEY,
    alert_id UUID,
    log_date TIMESTAMP,
    log_message TEXT
);
