tables.table_schema:
CREATE TABLE Investment_Risk_Models (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    risk_score FLOAT
);

CREATE TABLE `Investment_Risk_Model` (
  `id` INT PRIMARY KEY,
  `model_name` VARCHAR(50),
  `model_description` TEXT
);

CREATE TABLE `Portfolio_Optimization` (
  `id` INT PRIMARY KEY,
  `portfolio_name` VARCHAR(50),
  `portfolio_value` DECIMAL(10, 2),
  `risk_score` DECIMAL(5, 2)
);

CREATE TABLE RiskMetrics (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    metric_name VARCHAR(255),
    value FLOAT
);

CREATE TABLE StockPrices (
    id INT PRIMARY KEY,
    ticker VARCHAR(10),
    date DATE,
    price FLOAT
);

CREATE TABLE PortfolioAssets (
    id INT PRIMARY KEY,
    asset_id INT,
    portfolio_id INT,
    weight FLOAT
);

CREATE TABLE PortfolioRisk (
    id INT PRIMARY KEY,
    portfolio_id INT,
    risk_score FLOAT
);

CREATE TABLE Risk_Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50),
    model_description TEXT
);

CREATE TABLE Investments (
    investment_id INT PRIMARY KEY,
    investment_name VARCHAR(50),
    investment_type VARCHAR(50),
    investment_amount DECIMAL(10,2)
);

CREATE TABLE Portfolios (
    portfolio_id INT PRIMARY KEY,
    portfolio_name VARCHAR(50),
    portfolio_description TEXT
);
