tables.table_schema:
CREATE TABLE tax_deadlines (
    tax_entity_id INT PRIMARY KEY,
    tax_entity_name VARCHAR(50),
    filing_deadline DATE
);

CREATE TABLE tax_entities_deadlines (
    entity_id UUID PRIMARY KEY,
    entity_name STRING,
    filing_deadline DATE
);

CREATE TABLE tax_entities_contacts (
    contact_id UUID PRIMARY KEY,
    entity_id UUID REFERENCES tax_entities_deadlines(entity_id),
    contact_name STRING,
    contact_email STRING
);

CREATE TABLE tax_entities_deadlines (
    tax_entity_id INT PRIMARY KEY,
    entity_name STRING,
    filing_deadline DATE
);

CREATE TABLE tax_office_contacts (
    office_id INT PRIMARY KEY,    
    office_name STRING,
    contact_name STRING,
    contact_email STRING
);

CREATE TABLE tax_forms_info (
    form_id INT PRIMARY KEY,
    form_name STRING,
    form_description STRING,
    deadline_extension BOOLEAN
);

CREATE TABLE tax_entities_deadlines (
    id INT PRIMARY KEY,
    entity_name TEXT NOT NULL,
    filing_deadline DATE NOT NULL
);

CREATE TABLE tax_entities (
    id INT PRIMARY KEY,
    entity_name TEXT NOT NULL,
    business_type TEXT,
    registered_address TEXT
);

CREATE TABLE tax_entities (
  tax_id SERIAL PRIMARY KEY,
  entity_name VARCHAR(50) NOT NULL,
  filing_deadline DATE NOT NULL
);

CREATE TABLE reminders (
  reminder_id SERIAL PRIMARY KEY,
  filing_id INT NOT NULL,
  reminder_date DATE NOT NULL,
  description TEXT
);

CREATE TABLE penalties (
  penalty_id SERIAL PRIMARY KEY,
  filing_id INT NOT NULL,
  penalty_type VARCHAR(30) NOT NULL,
  penalty_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) UNIQUE NOT NULL,
  password VARCHAR(50) NOT NULL,
  email VARCHAR(50) NOT NULL
);
