tables.table_schema:
CREATE TABLE historical_tax_rates (
  id INT PRIMARY KEY,
  year INT,
  tax_rate DECIMAL
);

CREATE TABLE predicted_tax_trends (
  id INT PRIMARY KEY,
  year INT,
  predicted_tax_rate DECIMAL
);

CREATE TABLE tax_rates (
    year INT PRIMARY KEY,
    tax_rate FLOAT
);

CREATE TABLE tax_history (
    history_id INT PRIMARY KEY,
    year INT REFERENCES tax_rates(year),
    tax_rate FLOAT,
    tax_category VARCHAR(50)
);

CREATE TABLE tax_predictions (
    prediction_id INT PRIMARY KEY,
    prediction_year INT,
    predicted_tax_rate FLOAT,
    confidence_score FLOAT
);

CREATE TABLE tax_rates (
    tax_id INT PRIMARY KEY,
