tables.table_schema:
CREATE TABLE tax_regulations (
    id SERIAL PRIMARY KEY,
    country_code VARCHAR(2) NOT NULL,
    regulation_text TEXT NOT NULL,
    last_updated TIMESTAMP
);

CREATE TABLE tax_regulations (
    id SERIAL PRIMARY KEY,
    country_code VARCHAR(2) NOT NULL,
    regulation_text TEXT NOT NULL,
    last_updated TIMESTAMP
);

CREATE TABLE tax_compliance_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT,
    regulation_last_updated TIMESTAMP
);

CREATE TABLE country_tax_rates (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(255),
    tax_rate DECIMAL(5,2),
    tax_category VARCHAR(50)
);

CREATE TABLE tax_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100) NOT NULL,
    description TEXT,
    publication_date DATE
);

CREATE TABLE tax_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE regulation_references (
    reference_id INT PRIMARY KEY,
    regulation_id INT,
    reference_url VARCHAR(255),
    FOREIGN KEY (regulation_id) REFERENCES tax_regulations(regulation_id)
);
