tables.table_schema:
CREATE TABLE policyholders (
    id INT PRIMARY KEY,
    name TEXT,
    address TEXT,
    contact_details TEXT
);

CREATE TABLE policies (
    policy_id INT PRIMARY KEY,
    policy_type TEXT,
    policyholder_id INT,
    FOREIGN KEY (policyholder_id) REFERENCES policyholders(id)
);

CREATE TABLE Policyholders (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    address TEXT,
    contact_number VARCHAR(20)
);

CREATE TABLE policyholders (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL,
    phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE policy_plans (
    plan_id SERIAL PRIMARY KEY,
    plan_name VARCHAR(50) NOT NULL,
    coverage_amount DECIMAL(10,2) NOT NULL,
    premium DECIMAL(10,2) NOT NULL
);

CREATE TABLE policy_claim (
    claim_id SERIAL PRIMARY KEY,
    policy_id INT NOT NULL,
    claim_amount DECIMAL(10,2) NOT NULL,
    claim_date DATE NOT NULL
);

CREATE TABLE policy_transactions (
    transaction_id SERIAL PRIMARY KEY,
    policy_id INT NOT NULL,
    transaction_type VARCHAR(20) NOT NULL,
    transaction_amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE policyholders (
  policyholder_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  address VARCHAR(100) NOT NULL,
  phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE beneficiaries (
  beneficiary_id INT PRIMARY KEY,
  policy_id INT REFERENCES policies(policy_id),
  name VARCHAR(50) NOT NULL,
  relationship VARCHAR(50) NOT NULL
);

CREATE TABLE payments (
  payment_id INT PRIMARY KEY,
  policy_id INT REFERENCES policies(policy_id),
  payment_date DATE NOT NULL,
  amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE claims (
  claim_id INT PRIMARY KEY,
  policy_id INT REFERENCES policies(policy_id),
  claim_date DATE NOT NULL,
  description VARCHAR(255) NOT NULL
);
