tables.table_schema:
CREATE TABLE policy_information (
  policy_id INT PRIMARY KEY,
  applicant_name VARCHAR(255),
  policy_type VARCHAR(50),
  policy_status VARCHAR(50)
);

CREATE TABLE risk_factors (
  factor_id INT PRIMARY KEY,
  factor_name VARCHAR(50) NOT NULL,
  weight FLOAT NOT NULL
);

CREATE TABLE policy_applications (
  application_id SERIAL PRIMARY KEY,
  applicant_name VARCHAR(100) NOT NULL,
  policy_type VARCHAR(50) NOT NULL,
  application_date DATE NOT NULL
);

CREATE TABLE policy_eligibility (
  eligibility_id SERIAL PRIMARY KEY,
  application_id INT REFERENCES policy_applications(application_id),
  factor_id INT REFERENCES risk_factors(factor_id),
  eligibility_status BOOLEAN NOT NULL
);

CREATE TABLE RiskFactors (
    id INT PRIMARY KEY,
    age INT,
    credit_score INT,
    income DECIMAL
);

CREATE TABLE PolicyEligibility (
    id INT PRIMARY KEY,
    customer_id INT,
    policy_type VARCHAR(255),
    is_eligible BOOLEAN
);

CREATE TABLE CustomerData (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    address VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE risk_factors (
  risk_id UUID PRIMARY KEY,
  factor_name VARCHAR(100),
  weight DECIMAL(5, 2)
);
