tables.table_schema:
CREATE TABLE report_history (
 report_id UUID PRIMARY KEY,
 report_name VARCHAR(255),
 generation_date TIMESTAMP,
 report_data JSON
);

CREATE TABLE ReportPolicies (
    id INT,
    policy_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE ReportCustomers (
    customer_id INT,
    customer_name TEXT,
    email TEXT
);

CREATE TABLE PolicyData (
    policy_id INT,
    customer_id INT,
    premium_amount DECIMAL(10,2),
    policy_status TEXT
);

CREATE TABLE CustomerFeedback (
    feedback_id INT,
    customer_id INT,
    satisfaction_rating INT,
    feedback_text TEXT
);

CREATE TABLE ReportMetrics (
    metric_id INT,
    metric_name TEXT,
    value DECIMAL(10,2)
);
