tables.table_schema:
CREATE TABLE StockAnalysis (
    analysis_id UUID PRIMARY KEY,
    stock_id UUID REFERENCES HistoricalStockData(stock_id),
    analysis_date DATE NOT NULL,
    analysis_result TEXT
);

CREATE TABLE historical_stock_data (
    date DATE NOT NULL,
    ticker_symbol VARCHAR(10) NOT NULL,
    close_price DECIMAL NOT NULL,
    volume INT NOT NULL
);

CREATE TABLE stock_forecasts (
    date DATE NOT NULL,
    ticker_symbol VARCHAR(10) NOT NULL,
    forecasted_price DECIMAL NOT NULL
);

CREATE TABLE stock_prices ( 
    price_id INT PRIMARY KEY, 
    stock_id INT, 
    price DECIMAL(10, 2), 
    price_date DATE 
);

CREATE TABLE stock_investors (
    investor_id INT NOT NULL,
    investor_name VARCHAR(50) NOT NULL,
    investing_since DATE NOT NULL,
    investment_amount DECIMAL(12, 2) NOT NULL
);

CREATE TABLE stock_companies (
    company_id INT NOT NULL,
    company_name VARCHAR(50) NOT NULL,
    industry VARCHAR(50) NOT NULL
);

CREATE TABLE stock_analysis (
    analysis_id INT NOT NULL,
    stock_symbol VARCHAR(10) NOT NULL,
    analysis_date DATE NOT NULL,
    analysis_result VARCHAR(255) NOT NULL
);
