tables.table_schema:
CREATE TABLE investors (
  investor_id INT PRIMARY KEY,
  investor_name VARCHAR(50),
  investment_amount DECIMAL,
  investment_date DATE
);

CREATE TABLE Investors (
    investor_id INT PRIMARY KEY,
    investor_name TEXT
);

CREATE TABLE Investors (
    investor_id BIGINT PRIMARY KEY,
    investor_name VARCHAR(255) NOT NULL,
    investment_amount DECIMAL NOT NULL
);

CREATE TABLE Assets (
    asset_id BIGINT PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    asset_type VARCHAR(50) NOT NULL,
    asset_value DECIMAL NOT NULL
);

CREATE TABLE Portfolio (
    portfolio_id BIGINT PRIMARY KEY,
    investor_id BIGINT REFERENCES Investors(investor_id),
    asset_id BIGINT REFERENCES Assets(asset_id),
    allocation DECIMAL NOT NULL
);

CREATE TABLE Performance (
    performance_id BIGINT PRIMARY KEY,
    asset_id BIGINT REFERENCES Assets(asset_id),
    performance_date DATE NOT NULL,
    return_rate DECIMAL NOT NULL
);
