tables.table_schema:
CREATE TABLE InvestmentFirms (
  firm_id UUID PRIMARY KEY,
  firm_name VARCHAR(255) NOT NULL,
  firm_location VARCHAR(255) NOT NULL
);

CREATE TABLE MarketTrends (
  trend_id UUID PRIMARY KEY,
  trend_category VARCHAR(255) NOT NULL,
  trend_description TEXT,
  trend_date TIMESTAMP
);

CREATE TABLE MarketTrends ( 
   market_id INT PRIMARY KEY, 
   symbol TEXT NOT NULL, 
   trend_score DECIMAL, 
   industry TEXT 
);

CREATE TABLE RiskAssessments ( 
   assessment_id INT PRIMARY KEY, 
   market_id INT NOT NULL, 
   risk_score DECIMAL, 
   assessment_date DATE 
);

CREATE TABLE InvestmentDecisions ( 
   decision_id INT PRIMARY KEY, 
   assessment_id INT NOT NULL, 
   decision_date DATE, 
   investment_action TEXT 
);

