tables.table_schema:
CREATE TABLE features_table (
    feature_id INT PRIMARY KEY,
    feature_name VARCHAR(255),
    feature_value FLOAT
);

CREATE TABLE labels_table (
    label_id INT PRIMARY KEY,
    label_name VARCHAR(255),
    label_value FLOAT
);

CREATE TABLE features (
    id INT PRIMARY KEY,
    feature1 INT,
    feature2 VARCHAR(50),
    feature3 FLOAT
);

CREATE TABLE labels (
    id INT PRIMARY KEY,
    label1 VARCHAR(50),
    label2 INT
);

CREATE TABLE data (
    id INT PRIMARY KEY,
    features_id INT REFERENCES features(id),
    labels_id INT REFERENCES labels(id)
);

CREATE TABLE features (
    id SERIAL PRIMARY KEY,
    feature_1 INT,
    feature_2 INT,
    feature_3 INT
);

CREATE TABLE samples (
    id SERIAL PRIMARY KEY,
    sample_name VARCHAR(50),
    feature_id INT REFERENCES features(id)
);

CREATE TABLE labels (
    id SERIAL PRIMARY KEY,
    label_name VARCHAR(50)
);

CREATE TABLE feature_labels (
    feature_id INT REFERENCES features(id),
    label_id INT REFERENCES labels(id)
);

CREATE TABLE features (
    feature_id INT PRIMARY KEY,
    feature_name VARCHAR(255) NOT NULL,
    feature_value FLOAT NOT NULL
);

CREATE TABLE data_samples (
    sample_id INT PRIMARY KEY,
    feature_id INT NOT NULL,
    sample_data JSONB NOT NULL
);

CREATE TABLE labels (
    label_id INT PRIMARY KEY,
    sample_id INT NOT NULL,
    label_name VARCHAR(50) NOT NULL
);

CREATE TABLE predictions (
    prediction_id INT PRIMARY KEY,
    sample_id INT NOT NULL,
    prediction_value FLOAT NOT NULL,
    prediction_time TIMESTAMP NOT NULL
);

CREATE TABLE model_info (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(100) NOT NULL,
    model_type VARCHAR(50) NOT NULL,
    model_accuracy FLOAT NOT NULL
);
