tables.table_schema:
CREATE TABLE AggregateData (
    id INT PRIMARY KEY,
    feature1 VARCHAR(255),
    feature2 INT,
    feature3 DECIMAL
);
CREATE TABLE SummarizedData (
    id INT PRIMARY KEY,
    aggregate_id INT,
    summary_type VARCHAR(50),
    summary_value DECIMAL
);

CREATE TABLE feature_summary (
    id INT PRIMARY KEY,
    feature_name VARCHAR(255),
    feature_value DECIMAL,
    feature_type VARCHAR(50)
);

CREATE TABLE data_aggregation (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    value DECIMAL,
    aggregation_type VARCHAR(50)
);

CREATE TABLE aggregate_data (
    id INT PRIMARY KEY,
    date DATE,
    value FLOAT
);

CREATE TABLE summary_data (
    id INT PRIMARY KEY,
    date DATE,
    total_value FLOAT,
    avg_value FLOAT
);

CREATE TABLE predictive_model_features (
    id INT PRIMARY KEY,
    feature_name VARCHAR(50),
    feature_value FLOAT
);

CREATE TABLE model_predictions (
    id INT PRIMARY KEY,
    date DATE,
    prediction_value FLOAT
);
