tables.table_schema:
CREATE TABLE SamplingTechniques (
    technique_id INT PRIMARY KEY,
    technique_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE data_sampling_techniques (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label VARCHAR
);

CREATE TABLE sampling_methods (
    method_id INT PRIMARY KEY,
    method_name VARCHAR,
    description TEXT
);

CREATE TABLE sampling_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR,
    calculation_method VARCHAR,
    result FLOAT
);

CREATE TABLE sampling_techniques (
    id INT PRIMARY KEY,
    method VARCHAR(50),
    target_table VARCHAR(50)
);

CREATE TABLE sampling_params (
    param_id INT PRIMARY KEY,
    param_name VARCHAR(50),
    param_value FLOAT
);

CREATE TABLE dataset_samples (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label BOOLEAN
);

CREATE TABLE stratified_sampling (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label BOOLEAN
);

CREATE TABLE systematic_sampling (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label BOOLEAN
);

CREATE TABLE random_sampling (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label BOOLEAN
);

CREATE TABLE undersampling (
    id INT PRIMARY KEY,
    feature_1 FLOAT,
    feature_2 FLOAT,
    label BOOLEAN
);
