CREATE TABLE MetricsData (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    metric_value FLOAT
);

CREATE TABLE OperationsData (
    operation_id INT PRIMARY KEY,
    operation_name VARCHAR(255),
    operation_type VARCHAR(50)
);

CREATE TABLE AnalysisResults (
    result_id INT PRIMARY KEY,
    analysis_name VARCHAR(255),
    analysis_value FLOAT
);

CREATE TABLE operations_metrics (
    timestamp TIMESTAMP,
    user_id INT,
    operation_type TEXT,
    duration_seconds FLOAT
);

CREATE TABLE user_profile (
    user_id INT PRIMARY KEY,
    first_name TEXT,
    last_name TEXT,
    email TEXT
);

CREATE TABLE user_activity (
    timestamp TIMESTAMP,
    user_id INT,
    activity_type TEXT
);

CREATE TABLE operational_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    metric_value DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE department_metrics (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50),
    metric_id INT,
    FOREIGN KEY (metric_id) REFERENCES operational_metrics(metric_id)
);

CREATE TABLE employee_metrics (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    department_id INT,
    FOREIGN KEY (department_id) REFERENCES department_metrics(department_id)
);

CREATE TABLE project_metrics (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50),
    department_id INT,
    FOREIGN KEY (department_id) REFERENCES department_metrics(department_id)
);

CREATE TABLE client_metrics (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(50),
    project_id INT,
    FOREIGN KEY (project_id) REFERENCES project_metrics(project_id)
);
