tables.table_schema:
CREATE TABLE unstructured_data_table (
  id INT PRIMARY KEY,
  data JSONB
);

CREATE TABLE metadata_table (
  id INT PRIMARY KEY,
  data TEXT
);

CREATE TABLE indexing_table (
  id INT PRIMARY KEY,
  keywords JSONB
);

CREATE TABLE unstructured_data_records (
    id INT PRIMARY KEY,
    data JSONB
);

CREATE TABLE data_tags (
    tag_id SERIAL PRIMARY KEY,
    tag_name TEXT UNIQUE
);

CREATE TABLE data_mapping (
    mapping_id SERIAL PRIMARY KEY,
    data_id INT REFERENCES unstructured_data_records(id),
    tag_id INT REFERENCES data_tags(tag_id)
);

CREATE TABLE data_metadata (
    metadata_id SERIAL PRIMARY KEY,
    data_id INT REFERENCES unstructured_data_records(id),
    metadata_key TEXT,
    metadata_value TEXT
);

CREATE TABLE table1 (
    id INT PRIMARY KEY,
    data JSON
);

CREATE TABLE table2 (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    created_at TIMESTAMP
);

CREATE TABLE table3 (
    id INT PRIMARY KEY,
    content TEXT,
    tags JSONB
);

CREATE TABLE table4 (
    id INT PRIMARY KEY,
    url VARCHAR(255),
    metadata JSONB
);

CREATE TABLE table5 (
    id INT PRIMARY KEY,
    description TEXT,
    properties JSONB
);
