tables.table_schema:
CREATE TABLE real_time_data (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  data_type VARCHAR(50),
  value NUMERIC
);

CREATE TABLE real_time_streaming_data (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_timestamp TIMESTAMP,
    event_data JSONB
);

CREATE TABLE analysis_result (
    result_id INT PRIMARY KEY,
    event_id INT,
    result_description TEXT,
    analysis_timestamp TIMESTAMP
);

CREATE TABLE RealTimeStreams (
    stream_id UUID PRIMARY KEY,
    stream_name VARCHAR(50) NOT NULL,
    stream_data JSONB
);

CREATE TABLE StreamAnalytics (
    analytic_id UUID PRIMARY KEY,
    stream_id UUID REFERENCES RealTimeStreams(stream_id),
    analytic_type VARCHAR(30) NOT NULL,
    analytic_result JSONB
);

CREATE TABLE StreamStatistics (
    stat_id UUID PRIMARY KEY,
    stream_id UUID REFERENCES RealTimeStreams(stream_id),
    stat_timestamp TIMESTAMP NOT NULL,
    stat_value FLOAT
);

CREATE TABLE RealTimeData (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    data_payload JSONB
);

CREATE TABLE DataStreams (
    stream_id UUID PRIMARY KEY,
    stream_name TEXT,
    description TEXT
);

CREATE TABLE StreamAnalytics (
    analytics_id SERIAL PRIMARY KEY,
    stream_id UUID,
    analytics_type TEXT,
    result_data JSONB
);

CREATE TABLE DataConsumers (
    consumer_id SERIAL PRIMARY KEY,
    stream_id UUID,
    consumer_name TEXT,
    subscription_status BOOLEAN
);

CREATE TABLE RealTimeAnalytics (
    event_id UUID PRIMARY KEY,
    event_timestamp TIMESTAMP,
    event_type VARCHAR(50),
    event_data JSON
);

CREATE TABLE StreamSources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(100),
    source_type VARCHAR(50),
    source_location VARCHAR(255)
);

CREATE TABLE DataProcessing (
    process_id UUID PRIMARY KEY,
    process_name VARCHAR(100),
    process_type VARCHAR(50),
    process_status VARCHAR(50)
);

CREATE TABLE StreamConsumers (
    consumer_id UUID PRIMARY KEY,
    consumer_name VARCHAR(100),
    consumer_type VARCHAR(50),
    consumer_contact VARCHAR(100)
);

CREATE TABLE StreamResults (
    result_id UUID PRIMARY KEY,
    result_timestamp TIMESTAMP,
    result_data JSON
);
