tables.table_schema:
CREATE TABLE pipeline_data(
    id SERIAL PRIMARY KEY,
    data_key VARCHAR(50) NOT NULL,
    data_value TEXT NOT NULL
);

CREATE TABLE processed_data(
    id SERIAL PRIMARY KEY,
    pipeline_id INT NOT NULL,
    processed_value JSONB NOT NULL
);

CREATE TABLE DataIngestion (
    id INT PRIMARY KEY,
    source VARCHAR(255),
    timestamp TIMESTAMP,
    data JSONB
);

CREATE TABLE DataStorage (
    id INT PRIMARY KEY,
    stored_data JSONB,
    storage_timestamp TIMESTAMP
);

CREATE TABLE Ingestion (
    id INT PRIMARY KEY,
    data_source VARCHAR(255),
    ingestion_timestamp TIMESTAMP
);

CREATE TABLE Processing (
    id INT PRIMARY KEY,
    data_id INT, 
    processing_status VARCHAR(50),
    processing_timestamp TIMESTAMP
);

CREATE TABLE Storage (
    id INT PRIMARY KEY,
    processed_id INT, 
    storage_location VARCHAR(255),
    storage_timestamp TIMESTAMP
);

CREATE TABLE Logs (
    log_id INT PRIMARY KEY,
    activity_description TEXT, 
    log_timestamp TIMESTAMP
);

CREATE TABLE data_ingestion (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    data JSONB NOT NULL
);

CREATE TABLE data_processing (
    id SERIAL PRIMARY KEY,
    processing_date DATE NOT NULL,
    processed_data JSONB NOT NULL
);

CREATE TABLE data_storage (
    id SERIAL PRIMARY KEY,
    stored_date DATE NOT NULL,
    stored_data JSONB NOT NULL
);

CREATE TABLE pipeline_jobs (
    job_id SERIAL PRIMARY KEY,
    job_name VARCHAR(50) NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);

CREATE TABLE job_logs (
    log_id SERIAL PRIMARY KEY,
    job_id INT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_message TEXT
);
