tables.table_schema:
CREATE TABLE text_data (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE processing_tasks (
    task_id INT PRIMARY KEY,
    task_name TEXT,
    task_description TEXT,
    task_status TEXT
);

CREATE TABLE Texts (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE Nouns (
    id INT PRIMARY KEY,
    word_id INT,
    noun_text TEXT,
    FOREIGN KEY (word_id) REFERENCES Words (id)
);
