    preference_id INT PRIMARY KEY,
    user_id INT,
    preference_category VARCHAR(255),
    preference_value TEXT
);

CREATE TABLE user_interactions (
  user_id INT,
  interaction_type VARCHAR,
  timestamp TIMESTAMP
);

CREATE TABLE user_preferences (
  user_id INT,
  preference_type VARCHAR,
  preference_value VARCHAR
);

CREATE TABLE recommendations (
  recommendation_id INT,
  user_id INT,
  recommendation_text TEXT
);

CREATE TABLE UserInteractions (
    user_id INT PRIMARY KEY,
    interaction_time TIMESTAMP,
    interaction_type VARCHAR(50),
    interaction_details TEXT
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    preference_name VARCHAR(50),
    preference_value TEXT,
    last_updated TIMESTAMP
);

CREATE TABLE RecommendationHistory (
    user_id INT,
    recommended_item_id INT,
    recommendation_date DATE,
    recommendation_score FLOAT
);

CREATE TABLE ItemInteractions (
    item_id INT PRIMARY KEY,
    interaction_count INT,
    last_interaction_time TIMESTAMP
);

CREATE TABLE ItemDetails (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(100),
    item_description TEXT,
    category VARCHAR(50)
);
