tables.table_schema:
CREATE TABLE ImageData (
    id INT PRIMARY KEY,
    image_name VARCHAR(255),
    image_data BYTEA
);

CREATE TABLE ImageMetadata (
    image_id INT,
    metadata_key VARCHAR(255),
    metadata_value TEXT,
    FOREIGN KEY (image_id) REFERENCES ImageData(id)
);

CREATE TABLE image_data (
    image_id UUID PRIMARY KEY,
    image_url TEXT,
    image_data BYTEA
);

CREATE TABLE labels (
    label_id UUID PRIMARY KEY,
    label_name TEXT
);

CREATE TABLE image_labels (
    image_id UUID,
    label_id UUID,
    FOREIGN KEY (image_id) REFERENCES image_data(image_id),
    FOREIGN KEY (label_id) REFERENCES labels(label_id)
);

CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    image_url VARCHAR(255),
    image_data BYTEA
);

CREATE TABLE objects (
    object_id UUID PRIMARY KEY,
    object_name VARCHAR(50),
    object_coordinates JSONB,
    image_id UUID REFERENCES images(image_id)
);

CREATE TABLE labels (
    label_id UUID PRIMARY KEY,
    label_text VARCHAR(50),
    confidence DOUBLE PRECISION,
    object_id UUID REFERENCES objects(object_id)
);

CREATE TABLE categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE image_categories (
    image_id UUID REFERENCES images(image_id),
    category_id UUID REFERENCES categories(category_id),
    PRIMARY KEY(image_id, category_id)
);
