tables.table_schema:
CREATE TABLE knowledge_nodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(255) NOT NULL,
    node_type VARCHAR(50) NOT NULL
);

CREATE TABLE knowledge_edges (
    edge_id INT PRIMARY KEY,
    source_node INT NOT NULL,
    target_node INT NOT NULL,
    edge_type VARCHAR(50) NOT NULL
);

CREATE TABLE KnowledgeGraphNodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(255),
    node_type VARCHAR(50),
    created_date TIMESTAMP
);

CREATE TABLE Entity (entity_id SERIAL PRIMARY KEY,
entity_name VARCHAR NOT NULL,
entity_type VARCHAR NOT NULL);

CREATE TABLE KnowledgeGraphNodes (
  node_id SERIAL PRIMARY KEY,
  node_label TEXT NOT NULL
);

CREATE TABLE KnowledgeGraphAttributes (
  attribute_id SERIAL PRIMARY KEY,
  attribute_name TEXT NOT NULL,
  attribute_value TEXT
);

CREATE TABLE KnowledgeGraphMetadata (
  metadata_id SERIAL PRIMARY KEY,
  graph_id BIGINT NOT NULL,
  metadata_key TEXT NOT NULL,
  metadata_value TEXT
);
