tables.table_schema:
CREATE TABLE unified_view (
id INT,
first_name STRING,
last_name STRING,
age INT
);

CREATE TABLE datasets (
    id INT,
    name VARCHAR,
    source VARCHAR
);

CREATE TABLE visualizations (
    id INT,
    dataset_id INT,
    type VARCHAR,
    created_at TIMESTAMP
);

CREATE TABLE datasets (
    dataset_id INT PRIMARY KEY,
    dataset_name VARCHAR,
    source_id INT,
    dataset_type VARCHAR
);

CREATE TABLE sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR,
    source_type VARCHAR
);

CREATE TABLE visual_analysis (
    analysis_id INT PRIMARY KEY,
    dataset_id INT,
    source_id INT,
    analysis_result TEXT
);

CREATE TABLE dataset_A (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    age INT
);

CREATE TABLE dataset_B (
    id INT PRIMARY KEY,
    course VARCHAR(50),
    grade FLOAT
);

CREATE TABLE dataset_C (
    id INT PRIMARY KEY,
    department VARCHAR(50),
    manager VARCHAR(50)
);

CREATE TABLE dataset_D (
    id INT PRIMARY KEY,
    location VARCHAR(50),
    revenue FLOAT
);

CREATE TABLE dataset_E (
    id INT PRIMARY KEY,
    category VARCHAR(50),
    quantity INT
);
