tables.table_schema:
CREATE TABLE metadata_visualization (
    visualization_id INT PRIMARY KEY,
    visualization_name VARCHAR(255) NOT NULL,
    data_source_id INT NOT NULL,
    created_date TIMESTAMP NOT NULL
);

CREATE TABLE data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE metadata_visualizations (
    id INT PRIMARY KEY,
    visualization_name VARCHAR(255),
    data_source VARCHAR(255),
    created_date DATE
);

CREATE TABLE data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(255),
    connection_string TEXT,
    last_updated TIMESTAMP
);

CREATE TABLE user_activities (
    id INT PRIMARY KEY,
    user_id INT,
    activity_description VARCHAR(255),
    activity_date TIMESTAMP
);

CREATE TABLE data_integrity (
    id INT PRIMARY KEY,
    data_source_id INT,
    integrity_check VARCHAR(255),
    check_date TIMESTAMP
);

CREATE TABLE visualization_sources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    date_created TIMESTAMP NOT NULL
);

CREATE TABLE visualization_data (
    data_id UUID PRIMARY KEY,
    source_id UUID NOT NULL,
    data_value TEXT NOT NULL,
    data_date DATE NOT NULL
);

CREATE TABLE user_permissions (
    user_id UUID NOT NULL,
    source_id UUID NOT NULL,
    permission_type VARCHAR(20) NOT NULL,
    PRIMARY KEY (user_id, source_id)
);

CREATE TABLE data_sources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    data_format VARCHAR(50) NOT NULL
);

CREATE TABLE data_integrity_log (
    log_id UUID PRIMARY KEY,
    data_id UUID NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_message TEXT NOT NULL
);
