tables.table_schema:
CREATE TABLE anomalies_table (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    data_status VARCHAR(50),
    anomaly_type VARCHAR(50)
);

CREATE TABLE anomaly_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50),
    description TEXT
);

CREATE TABLE anomalies (
    id UUID PRIMARY KEY,
    type VARCHAR(50),
    description TEXT,
    severity INT
);

CREATE TABLE detected_anomalies (
    id UUID PRIMARY KEY,
    anomaly_id UUID REFERENCES anomalies(id),
    timestamp TIMESTAMP,
    location VARCHAR(100)
);

CREATE TABLE anomaly_logs (
    id UUID PRIMARY KEY,
    anomaly_id UUID REFERENCES anomalies(id),
    log_timestamp TIMESTAMP,
    log_message TEXT
);
