tables.table_schema:
CREATE TABLE EmployeeAttrition (
  employee_id INT,
  job_satisfaction FLOAT,
  salary FLOAT
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(255)
);

CREATE TABLE employees_department (
    employee_id INT,
    department_id INT,
    CONSTRAINT pk_emp_dept PRIMARY KEY (employee_id, department_id)
);

CREATE TABLE attrition_rates (
    employee_id INT,
    attrition_rate FLOAT,
    year INT,
    CONSTRAINT pk_attrition PRIMARY KEY (employee_id, year)
);
