tables.table_schema:
CREATE TABLE neural_network_experiments (
    experiment_id UUID PRIMARY KEY,
    hyperparameters JSONB,
    training_config JSONB
);

CREATE TABLE hyperparameters (
	hyperparameter_id INT PRIMARY KEY,
	hyperparameter_name STRING,
	hyperparameter_value STRING
);

CREATE TABLE hyperparameters (
    experiment_id UUID PRIMARY KEY,
    learning_rate FLOAT,
    batch_size INT,
    epochs INT
);

CREATE TABLE hyperparameters (
	hyperparam_id UUID PRIMARY KEY,
	hparam_name STRING,
	hparam_type STRING,
	hparam_value FLOAT
);

CREATE TABLE model_performance (
	model_id UUID PRIMARY KEY,
	exp_id UUID REFERENCES experiments(exp_id),
	validation_accuracy FLOAT,
	training_time INTERVAL
);

CREATE TABLE Hyperparameter (
	hyperparameter_id INT PRIMARY KEY,
	hyperparameter_name VARCHAR(50),
	hyperparameter_value FLOAT
);

CREATE TABLE Model (
	model_id INT PRIMARY KEY,
	model_name VARCHAR(50),
	model_type ENUM('CNN', 'RNN', 'DNN')
);

CREATE TABLE Experiment (
	experiment_id INT PRIMARY KEY,
	experiment_name VARCHAR(50),
	model_id INT,
	hyperparameter_id INT
);

CREATE TABLE Training (
	training_id INT PRIMARY KEY,
	experiment_id INT,
	loss FLOAT,
	epochs INT
);

CREATE TABLE Evaluation (
	evaluation_id INT PRIMARY KEY,
	experiment_id INT,
	accuracy FLOAT,
	loss FLOAT
);
