);

CREATE TABLE WordEmbeddings (
    id INT PRIMARY KEY,
    word VARCHAR(255) NOT NULL,
    embedding FLOAT[] NOT NULL
);

CREATE TABLE ModelInfo (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    trained_epoch INT NOT NULL,
    accuracy FLOAT NOT NULL,
    embedding_size INT NOT NULL
);

CREATE TABLE ModelMetrics (
    metrics_id INT PRIMARY KEY,
    model_id INT NOT NULL,
    precision FLOAT NOT NULL,
    recall FLOAT NOT NULL,
    f1_score FLOAT NOT NULL
);

CREATE TABLE InputText (
    text_id INT PRIMARY KEY,
    text VARCHAR(1000) NOT NULL,
    processed_text VARCHAR(1000) NOT NULL
);

CREATE TABLE ClassificationResult (
    result_id INT PRIMARY KEY,
    text_id INT NOT NULL,
    model_id INT NOT NULL,
    predicted_class VARCHAR(255) NOT NULL,
    confidence FLOAT NOT NULL
);
