tables.table_schema:
CREATE TABLE audio_spectrograms (
    spectrogram_id UUID PRIMARY KEY,
    audio_file_path VARCHAR(255) NOT NULL,
    spectrogram_data BYTEA NOT NULL,
    created_at TIMESTAMPTZ
);

CREATE TABLE audio_spectrograms (
    id INT PRIMARY KEY,
    spectrogram_data BYTEA,
    recording_id INT
);

CREATE TABLE spectrogram_training (
    id INT PRIMARY KEY,
    audio_data BYTEA,
    label VARCHAR(50)
);

CREATE TABLE spectrogram_testing (
    id INT PRIMARY KEY,
    audio_data BYTEA,
    label VARCHAR(50)
);

CREATE TABLE model_parameters (
    model_id INT PRIMARY KEY,
    learning_rate FLOAT,
    architecture JSON
);

CREATE TABLE training_logs (
    log_id INT PRIMARY KEY,
    model_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE testing_results (
    result_id INT PRIMARY KEY,
    model_id INT,
    test_accuracy FLOAT,
    confusion_matrix JSON
);
