tables.table_schema:
CREATE TABLE weather_data (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE weather_data (
    id INT,
    temperature FLOAT,
    humidity FLOAT,
    location VARCHAR(50)
);

CREATE TABLE climate_research (
    id INT,
    research_date DATE,
    researcher_name VARCHAR(50),
    findings TEXT
);

CREATE TABLE research_sites (
    id INT,
    site_name VARCHAR(50),
    location VARCHAR(50),
    activity TEXT
);

CREATE TABLE weather_station (
    station_id INT PRIMARY KEY,
    station_name TEXT NOT NULL,
    location TEXT NOT NULL
);

CREATE TABLE weather_conditions (
    condition_id INT PRIMARY KEY,
    data_id INT REFERENCES weather_data(data_id),
    condition TEXT NOT NULL
);

CREATE TABLE climate_researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT NOT NULL,
    affiliation TEXT NOT NULL
);
